//
// Mailchimp email API
//

$(function(){
    // Remove mailing list input placeholder text on focus
    $('#email-form').focusin(function(){
        $(this).attr('placeholder', '');
    });
    $('#email-form').focusout(function(){
        $(this).attr('placeholder', 'Your email here');
    });

    $(document).on('click', '.validation', function(){
        $(this).remove();
    });

    var emailVal = $('#email-form').val();

    $('#email-form').keyup(function(e){
        emailVal = $('#email-form').val();
        if(emailVal.length){
            $('#email-button').fadeIn('fast').css({display: 'inline-block'});
        } else {
            $('#email-button').fadeOut('fast');
        }
    });

    // API Subscribe
    $('#email-form').keydown(function (e){
        if(e.keyCode == 13){
            submitEmail();
        }
    });
    $(document).on('mousedown', '#email-button', function(){
        submitEmail();
    });

    function submitEmail(){
        emailVal = $('#email-form').val();
        // Basic email validation
        var validation = emailVal;
        if( /(.+)@(.+){2,}\.(.+){2,}/.test(validation) ){
            // Validation success
            $('.email-box .validation').remove();
            $('.email-box').append('<div id="validation" class="validation-success validation"><p>Thanks for joining our newsletter! You should receive a confirmation email shortly.</p></div>');
            $('#validation').hide().fadeIn('fast');
            $.ajax({
                url: '/wp-content/themes/banner/mailchimp-subscribe.php',
                type: 'POST',
                data: {email: emailVal}
            }).fail(function() {
                $('.email-box .validation').remove();
                $('.email-box').append('<div id="validation" class="validation-failed validation"><p>Sorry, it looks like there was a server error preventing you from signing up. We&#39;re looking into it.</p></div>');
                $('#validation').hide().fadeIn('fast');
            });
        } else {
            // Validation failed
            $('.email-box .validation').remove();
            $('.email-box').append('<div id="validation" class="validation-fail validation"><p>&#42; Sorry, it looks like you entered an invalid email address.</p></div>');
            $('#validation').hide().fadeIn('fast');
        }
    }
});

//
// Primary Javascript file
//

$(function() {
    //
    // Define variables
    //
    var docHeight     = $(document).height(),
        docWidth      = $(document).width(),
        headerWidth   = $('header').outerWidth(true) + $('header').position().left,
        headerHeight  = $('header').outerHeight(true) + $('header').position().top,
        infoBoxHeight = $('#info-box').outerHeight(true),
        infoBoxWidth  = $('#info-box').outerWidth(true),
        mobile        = false,
        mobileSize    = 800,
        breakpoint    = 800,
        randImage     = $('.home-background').attr('data-bg'),
        isHome        = $('.page-template-page-home-php').length;

    //
    // Call functions
    //

    setMobile();

    // Info box
    setInfoBox();

    // Check to make sure we don't cover up the header nav
    var infoBoxPosX = Math.floor((Math.random() * (docWidth - infoBoxWidth - headerWidth)) + headerWidth );
    var infoBoxPosY = Math.floor((Math.random() * (docHeight - infoBoxHeight - headerHeight - 120)) + headerHeight );

    if (infoBoxPosX > headerWidth){
        infoBoxPosY = Math.floor(Math.random() * (docHeight - infoBoxHeight - 120));
    } else {
        infoBoxPosY = Math.floor((Math.random() * (docHeight - infoBoxHeight - headerHeight - 120)) + headerHeight );
    }

    if (infoBoxPosY > headerHeight){
        infoBoxPosX = Math.floor(Math.random() * (docWidth - infoBoxWidth));
    } else {
        infoBoxPosX = Math.floor(Math.random() * (docWidth - infoBoxWidth - headerWidth) + headerWidth);
    }

    function setInfoBox(){
        if(window.outerWidth >= breakpoint){
            $(".draggable" ).draggable({
                cancel: "p, input"
            });

            $('#info-box').css({
                display: 'block',
                top: infoBoxPosY,
                left: infoBoxPosX
            });
        } else {
            // $(".draggable").draggable('destroy');
        }
    }

    // Back to top button
    $(document).on('click', '.to-top', function(){
        $('html, body').animate({scrollTop: 0});
    });

    // Disable hover on breadcrumbs if we have started scrolling
    $('nav').before('<div id="nav-disabler" class="nav-disabler"></div>');

    $(window).scroll(function(){
        if($(window).scrollTop() > 0){
            $('header, header nav').addClass('disabled');
            $('#nav-disabler').show();
        } else {
            $('header, header nav').removeClass('disabled');
            $('#nav-disabler').hide();
        }
    });

    // Nav hover
    $('#menu-primary-nav').mouseenter(function(){
        if(mobile === false){
            $('body:not(.home) header').after('<div id="fade-overlay" class="fade-overlay"></div>');
        }
    });
    $('#menu-primary-nav li:first-child').mouseenter(function(){
        showMenu();
    });
    $('#menu-primary-nav').mouseleave(function(){
        hideMenu();
    });

    $('nav').on('mouseenter', function(){
        $('header').addClass('fixed');
        // $('#nav-disabler').hide();
    });

    $('nav').on('mouseleave', function(){
        $('header').removeClass('fixed');
        // $('#nav-disabler').show();
    });

    // Nav hover at mobile
    $('#mobile-menu').on('click touchstart', function(e){
        e.preventDefault();
        $('nav').slideToggle();
    });

    //
    // Resize
    //

    if(isHome == 1){
        if(window.outerWidth >= breakpoint){
            $('#info-box').css({
                display: 'block',
                top: infoBoxPosY,
                left: infoBoxPosX
            });
        } else {
            // Mobile
            $('.content').prepend('<img class="mobile-image" src="' + randImage + '">');
        }

        $(window).resize(function(){
            setInfoBox();
            if(window.outerWidth >= breakpoint){
                // Not mobile
                $('.mobile-image').removeClass('hidden');
            } else {
                // Mobile
                $('.mobile-image').addClass('hidden');
                setInfoBox();
                $('.mobile-image').remove();
                $('.content').prepend('<img class="mobile-image" src="' + randImage + '">');
            }
        });
    }

    $(window).resize(function(){
        setMobile();
        if(window.outerWidth >= breakpoint){
            // Not mobile
            $('nav').css({display: 'block'});
        }
    });

    //
    // Define functions
    //

    function showMenu(){
        if(mobile === false){
            $('#menu-primary-nav').addClass('active');
            $('#breadcrumbs').hide();
        }
    }

    function hideMenu(){
        $('#menu-primary-nav').removeClass('active');
        $('#fade-overlay').remove();
        $('#breadcrumbs').show();
    }

    function setMobile(){
        if (window.outerWidth <= mobileSize) {
            mobile = true;
            $('body').addClass('mobile-layout');
        } else {
            mobile = false;
            $('body').removeClass('mobile-layout');
        }
    }
});

